window.onscroll = function(){onWindowScroll()};function onWindowScroll(){const sections = document.querySelectorAll('section');const observer = new IntersectionObserver(entries =>{entries.forEach(entry =>{if (entry.isIntersecting){entry.target.classList.add('visible');}});},{threshold:0.1});sections.forEach(section =>{observer.observe(section);});}document.addEventListener('DOMContentLoaded', () =>{const scrollToTopButton = document.createElement('button');scrollToTopButton.className = 'scroll-to-top hidden';scrollToTopButton.innerHTML = 'â†‘';document.body.appendChild(scrollToTopButton);scrollToTopButton.addEventListener('click', () =>{window.scrollTo({top:0, behavior:'smooth'});});window.addEventListener('scroll', () =>{if (window.scrollY>200){scrollToTopButton.classList.remove('hidden');scrollToTopButton.classList.add('visible');}else{scrollToTopButton.classList.remove('visible');scrollToTopButton.classList.add('hidden');}});});