window.onscroll = function(){onWindowScroll()};function onWindowScroll(){const sections = document.querySelectorAll('section');const observer = new IntersectionObserver(entries =>{entries.forEach(entry =>{if (entry.isIntersecting){entry.target.classList.add('visible');}});},{threshold:0.1});sections.forEach(section =>{observer.observe(section);});}function initLog(){const logMessages = document.querySelectorAll('.debug-log');logMessages.forEach(logMessage =>{logMessage.addEventListener("click", () =>{logMessage.style.transition = "opacity 0.5s";logMessage.style.opacity = "0";setTimeout(() =>{logMessage.remove();}, 500);});});}function initScrollTop(){const scrollToTopButton = document.createElement('button');scrollToTopButton.className = 'scroll-to-top hidden';scrollToTopButton.innerHTML = 'â†‘';document.body.appendChild(scrollToTopButton);scrollToTopButton.addEventListener('click', () =>{window.scrollTo({top:0, behavior:'smooth'});});window.addEventListener('scroll', () =>{if (window.scrollY>200){scrollToTopButton.classList.remove('hidden');scrollToTopButton.classList.add('visible');}else{scrollToTopButton.classList.remove('visible');scrollToTopButton.classList.add('hidden');}});}function initTomSelect(){new TomSelect("#countryCode",{create:true, sortField:{field:"text", direction:"asc"}});}document.addEventListener("DOMContentLoaded", () =>{initLog();initScrollTop();initTomSelect();});